mapper.select:
SELECT COUNT(*)
FROM user_table
WHERE oauth_id = #{oauth_id}

	SELECT COUNT(*) 
    FROM user_table
    WHERE email = #{email} AND oauth_id = #{id}

mapper.update:
UPDATE user_table
   SET point = point - #{amount}
 WHERE id = #{id}
   AND point >= #{amount}

UPDATE user_table
   SET point = point + #{amount}
 WHERE id = #{id}
